class Solution {
public:

    long long power(long long base, long long exp) {
        long long result = 1;
        while (exp > 0) {
            if (exp % 2 == 1) {
                result *= base;
            }
            base *= base;
            exp /= 2;     
        }
        return result;
    }

    int solve(int k ,int value,  int ind, int status, map<pair<pair<int,int>,int>,int>& dp){
        if(dp[{{value,ind},status}]!=0)
            return dp[{{value,ind},status}]-1;
        if(value>(k+5) || ind>30)
        return 0;
        else if(value>(k) && status==1)
        return 0;
        int ans=0;
        if(value==k){
            ans++;
        }
        if(status==0){
            ans+=solve(k,value-1,ind,1,dp);
        }
        ans+=solve(k,value+power(2,ind),ind+1,0,dp);
        dp[{{value,ind},status}]=ans+1;
        return ans;
    }

    int waysToReachStair(int k) {
        map<pair<pair<int,int>,int>,int>dp;
        return solve(k,1,0,0,dp);
    }
};
