class Solution {
public:
    int waysToReachStair(int k) {
        int i, j;
		int Diff = k - 1;
		int Result = (Diff <= 0);
		long long Now = 0;

		for(i = 0; i < 31; i++)
		{
			Now += (0x1 << i);

			if((Diff - Now) <= 0)
			{
				long long Temp = Now - Diff;

				if(Temp <= (i + 2))
				{
					long long A = 1;
					long long B = 1;

					if(Temp >= 16)
					{
						int C = 1;
						for(j = (i + 2); j > Temp; j--)
						{
							A *= j;
							A /= C;
							C++;
						}

						Result += A;
					}
					else
					{
						int C = (i + 2);
						for(j = 0; j < Temp; j++)
						{
							A *= C;
							A /= (j + 1);
							C--;
						}

						Result += A;
					}
				}
			}
		}

		return Result;
    }
};
