class Solution {
public:
    int solve(int n,int k) {
        if (k>n){
            return 0;
        }
        if (k==0 || k==n){
            return 1;
        }
        vector<vector<int>>vec(n+1,vector<int>(k+1,0));
        for(int i=0;i<=n;i++){
            for(int j=0;j<=min(i,k);j++){
                if (j==0 || j==i) {
                    vec[i][j]=1;
                }
                else{
                    vec[i][j]=vec[i-1][j-1]+vec[i-1][j];
                }
            }
        }
        return vec[n][k];
    }
    int waysToReachStair(int k) {
        int ans=0;
        for(int i=0;i<32;i++){
            int n=i+1;
            long long temp=(1LL<<i)-k;
            if (temp<0 || temp>n){
                continue;
            }
            ans+=solve(n,temp);
        }
        return ans;
    }
};
