class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def numWays(i, jump):
            if i-1 > k:
                return 0
            ret = 0
            if i==k:
                ret += 1
            ret += numWays(i+jump, jump*2)

            if i-1 >= 0:
                if i-1==k:
                    ret += 1
                ret += numWays(i-1+jump, jump*2)
            return ret

        return numWays(1, 1)
