class Solution {
private:
    int f(int s, int j, int flag, int k, map<vector<int>,int> &mp) {
        if(s > k+1) return 0;
        if(s > k && flag == 0) return 0;
        if(mp.find({s,j,flag}) != mp.end()) return mp[{s,j,flag}];

        int choice1 = 0, choice2 = 0;

        choice1 = f(s+pow(2,j),j+1,1,k,mp);
        if(flag == 1 && s > 0) choice2 = f(s-1,j,0,k,mp);

        if(s == k) return mp[{s,j,flag}] = 1 + (choice1 + choice2);
        return mp[{s,j,flag}] = choice1 + choice2;
    }
public:
    int waysToReachStair(int k) {
        map<vector<int>,int> mp;
        return f(1,0,true,k,mp);
    }
};
