#define deb(x) cout<<#x<<"="<<x<<endl
#define deb2(x, y) cout<<#x<<"="<<x<<","<<#y<<"="<<y<<endl
class Solution {
public:
    long long h(map<vector<long long>,long long>& m,vector<long long> temp,int& k){
        long long stair=temp[0],jump=temp[1],last=temp[2];
        // deb2(stair,jump);
        // deb(last);
        if(stair>=k+2) return 0;
        if(m.find(temp)!=m.end()) return m[temp];
        long long ans=0;
        if(stair==k) ans++;
        if(last==0 and stair!=0){
            ans+=h(m,{stair-1,jump,1},k);
        }
        ans+=h(m,{stair+jump,jump*2,0},k);
        m[temp]=ans;
        // deb2(stair,jump);
        // deb2(last,ans);
        return m[temp];
    }
    int waysToReachStair(int k) {
        map<vector<long long>,long long> m;
        long long ans=0;
        long long stair=1,jump=1,last=0;
        return h(m,{stair,jump,last},k);
    }
};
