class Solution {
public:
    long long solve(int k,long long i,long long jump,long long op1,unordered_map<string,long long> &m){
        if(i>k+1) return 0;
        long long uop1=0,uop2=0,x=0;
        string y=to_string(i)+to_string(jump)+to_string(op1);
        if(m[y]>0) return m[y]-1;
        if(i==k) x++;
        if(op1&&i>0) uop1=solve(k,i-1,jump,0,m);
        uop2=solve(k,i+pow(2,jump),jump+1,1,m);
        m[y]=uop1+uop2+x+1;
        return uop1+uop2+x;
    }
    int waysToReachStair(int k) {
        unordered_map<string,long long> m;
        return solve(k,1,0,1,m);
    }
};
