class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def solve(curr,jump):
            if curr<0 or jump//4>k:
                return 0

            if curr==k:
                return 1+solve(curr+jump,jump*2)+solve(curr+jump-1,jump*2)
            else:
                return solve(curr+jump,jump*2)+solve(curr+jump-1,jump*2)
        return solve(1,1)+solve(0,1)
