class Solution {
public:
    int powe(int n)
    {
        if(n==0) return 1;
        if(n%2!=0) return 2*powe(n-1);
        int ans=powe(n/2);
        return ans*ans;
    }
    int solve(int i,int jump,int k,unordered_map<int,int>& dp)
    {
        if(i>k+1) return 0;
        if(i>5&&dp.find(i)!=dp.end()) return dp[i];
        int ans=0;
        if(i==k||i==k+1) ans++;
        ans+=solve(i-1+powe(jump),jump+1,k,dp);
        ans+=solve(i+powe(jump),jump+1,k,dp);
        return dp[i]=ans;
    }
    int waysToReachStair(int k) {
        // if(k==0) return 2;
        // if(k==1) return 4;
         //if(k==2) return 3;
        unordered_map<int,int> dp;
        return solve(1,0,k,dp);
    }
};
