class Solution {
public:
            map<int,map<int,map<int,int>>> mp;

    int waysToReachStair(int k) {
        mp.clear();
        return get(k,0,1,0);
    }
    int get(int k,int j,int cl,int ln){

        int ans = 0;
        if(cl>k+1)return 0;
        if(cl>k&&ln)return 0;

        if(mp.count(j)&&mp[j].count(cl)&&mp[j][cl].count(ln))return mp[j][cl][ln];
        if(cl==k)ans = 1;
        ans +=  get(k,j+1,cl+(1L<<j),0);

        if(ln||cl==0){
            // no neg step
            return mp[j][cl][ln]= ans;
        }
        else{
            ans+= get(k,j,cl-1,1);
        }
        return mp[j][cl][ln] = ans;
    }
};
