class Solution {
    unordered_map<int, vector<vector<int>>> dp;

    int hi(int k,int idx,int jumps,int fl) {
        if(idx>k+1 || jumps>=32) return 0;

        if(dp.count(idx) && dp[idx][jumps][fl]!=-1) return dp[idx][jumps][fl];

        int ans=0;
        if(idx==k) ans=1;

        if(fl){
            ans+=hi(k,idx-1,jumps,0);
        }

        if (idx+(pow(2,jumps))<= k+1){
            ans+=hi(k,idx+(pow(2,jumps)), jumps+1,1);
        }
        dp[idx].resize(32,vector<int>(2, -1));
        return dp[idx][jumps][fl] = ans;
    }
    public:
    int waysToReachStair(int k) {
        return hi(k,1,0,1);
    } 
};
