#include <unordered_map>
#include <vector>
using namespace std;

class Solution {
    unordered_map<int, vector<vector<int>>> memo;

    bool isOutOfBounds(int position, int target, int jump) {
        return position > target + 5 || jump > 31;
    }

    int solve(int target, int position, int jump, bool canMoveBack) {
        if (isOutOfBounds(position, target, jump)) return 0;

        if (memo.count(position) && memo[position][jump][canMoveBack] != -1)
            return memo[position][jump][canMoveBack];

        int ways = (position == target);

        if (canMoveBack)
            ways += solve(target, position - 1, jump, false);

        if (position + (1 << jump) <= target + 1)
            ways += solve(target, position + (1 << jump), jump + 1, true);

        if (!memo.count(position))
            memo[position] = vector<vector<int>>(32, vector<int>(2, -1));

        return memo[position][jump][canMoveBack] = ways;
    }

public:
    int waysToReachStair(int target) {
        return solve(target, 1, 0, true);
    }
};
