class Solution:
    def waysToReachStair(self, k: int) -> int:
        return self.dfs(1, k, 0, False)

    @lru_cache(None)
    def dfs(self, x: int, k: int, jump: int, back: bool) -> int:
        if x - k > 1:
            return 0
        res = 0
        if x == k:
            res = 1
        if x > 0 and not back:
            res += self.dfs(x - 1, k, jump, True)
        res += self.dfs(x + 2 ** jump, k, jump + 1, False)
        #print(x, k, jump, back, res)
        return res
