class Solution:
    @cache
    def f(self, k, i, J, O):
        if i > k + 5 or J > 31: return 0
        if (i, J, O) in self.D: return self.D[(i, J, O)]
        aa = (i == k) + (O and self.f(k, i - 1, J, 0)) + (i + 2**J <= k + 1 and self.f(k, i + 2**J, J + 1, 1))
        self.D[(i, J, O)] = aa
        return aa

    def waysToReachStair(self, k):
        self.D = defaultdict(int)
        return self.f(k, 1, 0, 1)
