class Solution {
public:

    unordered_map<int, vector<vector<int>>> dp;
     int helper(int k, int i, int jump, bool can) {
        if (i > k + 5)
            return 0;

        if (jump > 31)
            return 0;

        if (dp.count(i) && dp[i][jump][can] != -1)
            return dp[i][jump][can];

        int ans = (i == k);
        if (can)
            ans += helper(k, i - 1, jump, false);
        if (i + (1 << jump) <= k + 1)
            ans += helper(k, i + (1 << jump), jump + 1, true);

        dp[i].resize(32, vector<int>(2, -1));
        return dp[i][jump][can] = ans;
    }

    int waysToReachStair(int k) {
        dp.clear();
        return helper(k, 1, 0, true);
    }
};
