class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, jump, did_use_first_op):
            m_ret = 0
            if i == k:
                m_ret = 1
            if jump > 100:
                return m_ret

            m_f, m_s = 0, 0
            if i != 0 and not did_use_first_op:
                m_f = dp(i - 1, jump, True)
            m_s = dp(i + 2 ** jump, jump + 1, False)
            return m_ret + m_f + m_s

        return dp(1, 0, False)
