class Solution:

    def waysToReachStair(self, k):
        dp = {}
        def solve(k, i, jump, can):
            if i > k + 5:
                return 0
            if jump > 31:
                return 0

            if (i, jump, can) in dp:
                return dp[(i, jump, can)]

            ans = (i == k)

            if can:
                ans += solve(k, i - 1, jump, False)
            if i + (1 << jump) <= k + 1:
                ans += solve(k, i + (1 << jump), jump + 1, True)

            dp[(i, jump, can)] = ans
            return ans

        return solve(k, 1, 0, True)
