class Solution {
public:
    int ncr(int n, int k)
{
    int C[k + 1];
    memset(C, 0, sizeof(C));

    C[0] = 1; // nC0 is 1

    for (int i = 1; i <= n; i++)
    {
        for (int j = min(i, k); j > 0; j--)
            C[j] = C[j] + C[j - 1];
    }
    return C[k];
}
    int waysToReachStair(int k) {
        k--;
        int ans=0;
        for(int i=0;;i++){
            int v=0;
            int j=0;
            while(v<k+i){
                v+=(1LL<<j);
                j++;
            }
            if(v==k+i){
                ans+=ncr(j+1,i);
                // cout<<j+1<<" "<<i<<"\n";
            }
            int number=i;
            if(number>0) {
                int lam=1<<(number-1);
                if(lam>k+i+5) break;
            }
        }
        return ans;
    }
};
