class Solution {
public:
    int solve(int &k,bool x,int j,int p, unordered_map<string,int>&dp){
        if(p-k>2) return 0;
        int ans=0;
        if(p==k) ans=1;
        int w=pow(2,j);
        string s="";
        if(x){
            s.push_back(j+'0');
           s+=" ";
             s.push_back(p+'0');
            s+=" ";
            s.push_back('1');
        }
        else{
               s.push_back(j+'0');
           s+=" ";
             s.push_back(p+'0');
            s+=" ";
            s.push_back('0');
        }
        if(dp.find(s)!=dp.end()) return dp[s];
        if(x && p!=0){
           ans+=(solve(k,false,j,p-1,dp)+solve(k,true,j+1,p+w,dp)); 
        }
        else{
            ans+=solve(k,true,j+1,p+w,dp);
        }
        return dp[s]=ans;
    }
    int waysToReachStair(int k) {
        unordered_map<string,int>dp;
        return solve(k,true,0,1,dp);
    }
};
