class Solution {
public:
    int dp[40][3][1000];
    int rec(int stair, int jump, int cnt, int prev, int k)
    {
        if (stair==k) 
        {
            if (dp[jump][prev][cnt]!=-1) return dp[jump][prev][cnt];
            int ans = 0;
            if (prev)
            {
                ans += rec(stair + (1<<jump), jump+1, cnt+1, 1, k);
                ans += rec(stair-1, jump, cnt+1, 0, k);
            }
            else 
            {
                ans += rec(stair + (1<<jump), jump+1, cnt+1, 1, k); 
            }
            return dp[jump][prev][cnt] = 1 + ans;
        }
        if (dp[jump][prev][cnt]!=-1) return dp[jump][prev][cnt];
        if (stair<0) return 0;
        if (stair>k+1) return 0;
        int ans = 0;
        if (prev)
        {
            ans += rec(stair + (1<<jump), jump+1, cnt+1, 1, k);
            ans += rec(stair-1, jump, cnt+1, 0, k);
        }
        else 
        {
            ans += rec(stair + (1<<jump), jump+1, cnt+1, 1, k); 
        }
        return dp[jump][prev][cnt] = ans;

    }
    int waysToReachStair(int k) {
        for (int i=0; i<32; i++)
        {
            for (int j=0; j<3; j++)
            {
                for (int k=0; k<1000; k++) dp[i][j][k] = -1;
            }
        }
        return rec(1, 0, 0, 1, k);
    }
};
