class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4

        ans = 0
        for f in range(1, 31):
            for b in range(f + 2):
                if 2**f - b == k:
                    ans += comb(f + 1, b)

        return ans
