#define ll long long 

class Solution {

    ll nck(int n, int k) {
        if (k > n) return 0;
        if (k == 0 || k == n) return 1;

        k = min(k, n - k); 
        ll result = 1;

        for (int i = 0; i < k; ++i) {
            result *= (n - i);
            result /= (i + 1);
        }
        return result;
    }

public:
    int waysToReachStair(int k) {
        ll cnt = 0;
        ll sum = 0;

        ll ans = 0;
        for(ll i=1;sum<=k;i+=i) {
            sum+=i;
            sum--;
            cnt++;
        }

        for(ll i=0;i<cnt;i++) {
            sum = 1;
            ll n = 1;
            for(ll j=0;j<i;j++) {
                sum+=n;
                n+=n;
            }
            if(sum<k)
                continue;
            ll diff = sum-k;
            ll ones = i+1;
            ans += nck(ones,diff);
        }
        return ans;
    }
};
