class Solution:
    def waysToReachStair(self, k: int) -> int:
        cur = 1
        fac = [1]
        for i in range(1, 35):
            fac.append(fac[-1] * i)

        def binom(n, k):
            return fac[n] // fac[k] // fac[n - k]

        answer = 0
        n = 0
        for n in range(0, 35):
            l = 0
            r = n + 1
            c = (1 << n) - k
            if (l <= c and c <= r):
                answer += binom(r, c)

        return answer
