class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i,jump,prev_down):
            # print(i,jump,prev_down)
            if i<0:
                return 0
            if i==k:
                if prev_down:
                    return 1+dp(i+2**jump,jump+1,False)
                return 1+dp(i+2**jump,jump+1,False)+dp(i-1,jump,True)
            if prev_down and i>k:
                return 0
            if not prev_down and i-1>k:
                return 0
            if prev_down:
                return dp(i+2**jump,jump+1,False)
            return dp(i+2**jump,jump+1,False)+dp(i-1,jump,True)
        return dp(1,0,False)
