class Solution {
    unordered_map<int, vector<vector<int>>> dp;
    int rec(int k, int i, int jump, bool ifpos){
        if(i > k+4 || jump > 31){
            return 0;
        }

        if(dp.find(i) != dp.end() && dp[i][jump][ifpos] != -1){
            return dp[i][jump][ifpos];
        }

        int ans = (i == k);

        if(ifpos){
            ans += rec(k, i-1, jump, 0);
        }
        if(i + (1ll << jump) <= k+1){
            ans += rec(k, i + (1ll << jump), jump+1, 1);
        }

        dp[i].resize(32, vector<int> (2, -1));
        return dp[i][jump][ifpos] = ans;

    }
public:
    int waysToReachStair(int k) {
        return rec(k, 1, 0, 1);
    }
};
