#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
    unordered_map<int, vector<vector<int>>> part;

    int anss(int n, int b, int c, bool d) {
        if (b > n + 5)
            return 0;

        if(c > 31)
            return 0;

        if(part.count(b) && part[b][c][d] != -1)
            return part[b][c][d];

        int res = (b == n);
        if (d)
            res += anss(n, b - 1, c, 0);
        if (b + (1 << c) <= n + 1)
            res += anss(n, b + (1 << c), c + 1, 1);

        part[b].resize(32, vector<int>(2, -1));

        return part[b][c][d] = res;
    }

public:
    int waysToReachStair(int k) {
        return anss(k, 1, 0, 1);
    }
};
