class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def count(i=1, jump=0, cool=True):
            if i > k + 1:
                return 0
            res = 1 if i == k else 0
            res += count(i + 2 ** jump, jump + 1, True)
            if cool:
                res += count(i - 1, jump, False)
            return res
        return count()
