class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dp(pos, jump, alreadyDown):
            if pos>k+1: return 0
            res=int(pos==k)
            if not alreadyDown and pos>0:
                res += dp(pos-1, jump, True)
            return res + dp(pos + (1<<jump), jump+1, False)
        return dp(1,0,False)
