class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def rec(stair, down, jump):
            if stair > k+1:
                return 0
            if down == True and stair != 0:
                a = rec(stair-1, False, jump)
            else:
                a = 0
            b = rec(stair + 2**jump, True, jump+1)
            if stair == k:
                c = 1
            else:
                c = 0
            return a + b + c

        return rec(1, True, 0)
