class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def helper(i, j, d=False):
            if i > k+1 or i < 0:
                return 0
            res = 0
            if i == k:
                res += 1
            if not d:
                res += helper(i-1, j, True)
            res += helper(i+2**j, j+1, False)
            # print(i, j, d, res)
            return res
        # print()
        res = helper(1, 0)
        return helper(1, 0)
