/**
 * @param {number} k
 * @return {number}
 */
var waysToReachStair = function(k) {

  const f = memoize((i, jump, wentDown) => {

    let ways = 0;
    if (i === k) ways += 1;
    if (i - 1 > k) return ways;

    if (i > 0 && !wentDown) ways += f(i - 1, jump, true);
    ways += f(i + 2 ** jump, jump + 1, false);
    return ways;
  });

  return f(1, 0, false);
};

const memoize = (fn) => {
  const memo = new Map();

  return (...args) => {

    const key = args.map(JSON.stringify).join(",");
    if (memo.has(key)) return memo.get(key);

    const result = fn(...args);
    memo.set(key, result);

    return result;
  }
}
