#define ll long long
class Solution
{
public:
    ll waysToReachStair(ll k)
    {
        ll ans = 0;
        auto _ = [&](ll n, ll k) -> ll
        {
            if (k > n)  return 0;
            if (k == 0 or k == n)   return 1;   k = min(k, n - k);
            ll c = 1;
            for (ll i = 0; i < k; ++i) c = c * (n - i) / (i + 1);
            return c;
        };
        for (ll i = 0; i < 32; i++)
        {
            if (((1 << i) - k) < 0) continue;
            ans += _(i + 1, (1 << i) - k);
        }
        return ans;
    }
};
