class Solution {
public:
    int waysToReachStair(int m) {
        int res=0;
        vector<unordered_map<int, int>> clear(31);
        vector<unordered_map<int, int>> used(31);
        clear[0][1]=1;

        while(true)
        {
            bool b=0;
            for(int i=0; i<30; i++)
            {
                unordered_map<int,int> toDelete;
                for(auto& [k,v]: clear[i])
                {
                    b=1;
                    if(k==m) res+=v;
                    if(k>0)
                    {
                        used[i][k-1]+=v;
                    }
                    if(k+pow(2, i)<=m+2)
                    {
                        clear[i+1][k+pow(2, i)]+=v;
                    }
                    toDelete[k]+=v;
                }
                for(auto& [l,j]: toDelete)
                {
                    clear[i][l]-=j;
                    if(clear[i][l]==0) clear[i].erase(l);
                }
                toDelete.clear();
                for(auto& [k, v]: used[i])
                {
                    b=1;
                    if(k==m) res+=v;
                    if(k+pow(2, i)<=m+2)
                    {
                        clear[i+1][k+pow(2, i)]+=v;
                    }
                    toDelete[k]+=v;
                }
                for(auto& [l,j]: toDelete)
                {
                    used[i][l]-=j;
                    if(used[i][l]==0) used[i].erase(l);
                }
            }
            if(b==0) break;
        }

        // for(auto& el: clear)
        // {
        //     res+=el[m];
        // }
        // for(auto& el: used)
        // {
        //     res+=el[m];
        // }
        return res;
    }
};
