class Solution:
    def waysToReachStair(self, k: int) -> int:


        @cache
        def dp(cn,cj, rd):
            if cn > k + 1:
                return 0
            if cn < 0:
                return 0

            co = 1 if cn == k else 0

            if not rd:
                co += dp(cn-1,cj, True)  
            co += dp(cn + (1<<cj), cj + 1,False) 
            return co

        return dp(1,0,False)
