from functools import lru_cache
class Solution:
    def waysToReachStair(self, k: int) -> int:

        # count = 0

        @lru_cache(None)
        def move(stair, went_down, jump):

            nonlocal k

            count = 0

            if stair == k:
                count += 1

            if stair > k + 1:
                return 0

            if stair > 0 and went_down == False:
                count += move(stair - 1, True, jump)

            count += move(stair + 2**jump, False, jump + 1)
            return count

        ans = move(1, False, 0)
        return ans
