#define ll long long
class Solution {
    map<ll,vector<vector<ll>>>dp;
    ll help(int target,int start,int jump,int take){
        if(start>target+1||jump>30)return 0;
        if(dp.find(start)!=dp.end()&&dp[start][jump][take]!=-1){
            return dp[start][jump][take];
        }
        dp[start].resize(32,vector<ll>(2,-1));
        ll res=0;
        if(start==target)res++;
        if(start+(1<<jump)<=target+1)res+=help(target,start+(1<<jump),jump+1,1);
        if(take){
            res+=help(target,start-1,jump,0);
        }
        return dp[start][jump][take]=res;
    }

public:
    int waysToReachStair(int k) {
        return help(k,1,0,1);
    } 
};
