class Solution {
public:
    int c(int n, int m){
        __int128 ans = 1;
        int max_val = max(m, n-m), min_val = min(m, n-m);
        for(int i=max_val+1;i<=n;i++)
            ans *= i;
        for(int i=2;i<=min_val;i++)
            ans /= i;
        return ans;
    }
    int waysToReachStair(int k) {
        int ans = 0;
        for(int i=0;i<=32;i++){
            long long val = k;
            for(int j=0;j<i;j++)
                val -= 1LL<<j;
            for(int j=0;j<=i+1;j++){
                if(val + j == 1){
                    ans += c(i+1, j);
                    // cout<<i+1<<" "<<j<<" "<<c(i+1, j)<<"\n";
                }
            }
        }
        return ans;
    }
};
