class Solution {
public:
    unordered_map<int,int> dp[30][2];
    int func(int step,int jump,int tight,int& k)
    {
        if(step<0) return 0;
        if(step>k+1) return 0;

        if(dp[jump][tight].find(step)!=dp[jump][tight].end())
            return dp[jump][tight][step];

        int ans=func(step+pow(2,jump),jump+1,0,k);
        if(step==k) ans++;
        if(!tight)
        ans+=func(step-1,jump,1,k);

        return dp[jump][tight][step]=ans;
    }
    int waysToReachStair(int k) {
        return func(1,0,0,k);
    }
};
