class Solution:
    def poss(self, a, b):
        val = 1
        deno = 1
        num = 1
        i = a
        j = a - b
        while i > b:
            num *= i
            deno *= j
            if num % deno == 0:
                val *= (num // deno)
                num = 1
                deno = 1
            i -= 1
            j -= 1
        return val
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4

        ans = 0
        for negSteps in range(41):
            for jmps in range(max(0, negSteps - 1), 41):
                rep = (1 << jmps)
                if rep - negSteps == k:
                    ans += self.poss(jmps + 1, negSteps)
        return ans
