class Solution {
public:
    int solve(int s, int k, int jump, int prev, map<int, map<int,map<int,int>>> &mp)
    {
        if(s==k)
            return 1;

        if(s>k)
        {
            if(s==k+1 && prev==0)
                return 1;

            return 0;
        }

        if(mp.find(s) != mp.end() && mp[s].find(jump) != mp[s].end() && mp[s][jump].find(prev) != mp[s][jump].end())
            return mp[s][jump][prev];

        int res = 0;

        if(!prev && s>0)
        {
            res += solve(s-1, k, jump, 1, mp);
        }

        res += solve(s+pow(2, jump), k, jump+1, 0, mp);

        return mp[s][jump][prev] = res;
    }
    int waysToReachStair(int k) 
    {
        if(k==0)
            return 2;

        if(k==1)
            return 4;

        if(k==2)
            return 4;

        map<int, map<int,map<int,int>>> mp;

        return solve(1,k,0,0, mp);
    }
};
