class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0

        for i in range(33):
            up = 1 << i
            diff = up - k
            gaps = i + 1

            if diff < 0 or gaps < diff:
                continue

            ways = 1
            if diff > gaps - diff:
                j1 = diff + 1
                while j1 <= gaps:
                    ways *= j1
                    j1 += 1
                for j in range(2, gaps - diff + 1):
                    ways //= j
            else:
                j1 = gaps - diff + 1
                while j1 <= gaps:
                    ways *= j1
                    j1 += 1
                for j in range(2, diff + 1):
                    ways //= j

            ans += ways

        return ans
