#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    unordered_map<int, vector<vector<int>>> dp;

    int solve(int k, int i, int j, bool c) {
        if (i > k + 5)
            return 0;

        if (j > 31)
            return 0;

        if (dp.count(i) && dp[i][j][c] != -1)
            return dp[i][j][c];

        int ans = (i == k) ? 1 : 0;
        if (c)
            ans += solve(k, i - 1, j, false);
        if (i + (1 << j) <= k + 1)
            ans += solve(k, i + (1 << j), j + 1, true);

        dp[i].resize(32, vector<int>(2, -1));
        dp[i][j][c ? 1 : 0] = ans;

        return ans;
    }

    int waysToReachStair(int k) {
        dp.clear();
        return solve(k, 1, 0, true);
    }
};
