class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def rec(pos, j, can):
            ans = 0
            if pos == k:
                ans += 1
            if pos > k + 1:
                return 0
            if can:
                ans += rec(pos - 1, j, False)
            ans += rec(pos + 2**j, j + 1, True)
            return ans

        return rec(1, 0, True)
