class Solution {
public:
    int waysToReachStair(int k) {

        vector<vector<int>> comb(32, vector<int>(32));
        for(int i = 0; i < 32; i++) {
            comb[i][0] = 1;
            for(int j = 1; j <= i; j++)
                comb[i][j] = comb[i - 1][j - 1] + comb[i - 1][j];
        }        

        int ans = 0;
        int p = 1, v = 1, type1 = 1;
        while(v - type1 <= k) {
            if(v >= k) {
                int minus = v - k;
                ans += comb[type1][minus];
            }
            v += p;
            type1++;
            p *= 2;
        }
        return ans;

    }
};
