class Solution {
public:
    unordered_map<int,vector<vector<int>>> dp;
    int Kanhaiya(int a,int b,int c,bool d){
        if(a>b+5)
                return 0;
        if(c>31)
                return 0;
        if(dp.count(a) && dp[a][c][d]!=-1)
            return dp[a][c][d];
        int e=(a==b);
        if(d)
        {
            e+=Kanhaiya(a-1,b,c,false);
        }
        if(a+(1<<c)<=b+1)
            e+=Kanhaiya(a+(1<<c),b,c+1,true);

        dp[a].resize(32,vector<int>(2,-1));
        return dp[a][c][d]=e;
    }
    int waysToReachStair(int k) {
        return Kanhaiya(1,k,0,true);
    }
};
