class Solution {
    unordered_map<int,vector<vector<int>>>ddpp;
    int solveKrNaa(int k, int i, int upppar, bool shiMai) {


        if(upppar > 31) {
            return 0;
        }

         if (i > k + 5) {
            return 0;
        }



         if(ddpp.count(i) && ddpp[i][upppar][shiMai] != -1) {
            return ddpp[i][upppar][shiMai];
        }


        int Annswar = (i == k);
        if (shiMai)
            Annswar += solveKrNaa(k, i - 1, upppar, 0);
        if (i + (1 << upppar) <= k + 1)
            Annswar += solveKrNaa(k, i + (1 << upppar), upppar + 1, 1);

        ddpp[i].resize(32, vector<int>(2, -1));

        return ddpp[i][upppar][shiMai] = Annswar;
    }

public:
    int waysToReachStair(int k) {
        return solveKrNaa(k, 1, 0, 1);
    }
};
