class Solution:
    def waysToReachStair(self, k: int) -> int:
        '''
        input: k

        0 <= k <= 10 ** 9


        options:

        can go down 1 stair if not used previously and i != 0

        go to stair 1 + 2 ** jump; jump += 1

        jump starts at 0

        STARTS at stair 1


        '''
        @cache
        def calc(stair, jump, prev_sub):

            if stair - 2 > k:
                return 0

            total = 0

            if stair == k:
                total += 1

            if prev_sub:
                total += calc(stair - 1, jump, False)

            total += calc(stair + 2 ** jump, jump + 1, True)

            return total


        return calc(1, 0, True)
