class Solution {
public:

    int solve(vector <vector <vector <int>>>& dp,int i,int j,int prev,int count){
        if(i==1){
            if(prev==1){
                return 1+solve(dp,i-pow(2,j),j+1,0,count);
            }
            return 1+solve(dp,i+1,j,1,count+1)+solve(dp,i-pow(2,j),j+1,0,count);
        }
        if(i<0){
            return 0;
        }
        if(dp[j][prev][count]!=-1){
            return dp[j][prev][count];
        }
        int ans=0;
        if(prev!=1){
            ans+=solve(dp,i+1,j,1,count+1);
        }
        ans+=solve(dp,i-pow(2,j),j+1,0,count);
        return dp[j][prev][count]=ans;
    }

    int waysToReachStair(int k) {
        vector <vector <vector <int>>> dp(100,(vector <vector <int>> (2, (vector <int> (100,-1)))));
        return solve(dp,k,0,0,0);
    }
};
