class Solution:
    def waysToReachStair(self, k: int) -> int:
        jump = 0
        curr = 1
        while curr <= k:
            jump += 1
            curr += 2 ** jump - jump + 1
        ans = 0
        pos = 1
        if k == 0 or k == 1:
            ans = 1
        for j in range(jump + 1):
            pos += 2 ** j
            op1 = j + 2
            step = pos - k
            if step <= op1 and step >= 0:
                ans += factorial(op1) // (factorial(op1 - step) * factorial(step))

        return ans
