class Solution:
    def waysToReachStair(self, k: int) -> int:
        d = {}
        def abc(c,j, flag):
            if c>k+1:
                return 0
            try:
                return d[c,j,flag]
            except:
                pass
            m = 0
            if c==k:
                m+=1

            if flag:
                m+= abc(c-1, j, flag^1)
            m+= abc(c+j, 2*j, 1)
            d[c,j,flag] = m
            return m

        return abc(1, 1, 1)
